import{_ as t,c as n,j as e,a as l,o as r}from"./chunks/framework.CqbvlPrP.js";const u=JSON.parse('{"title":"第十四章 将五种祸患关进笼子","description":"","frontmatter":{},"headers":[],"relativePath":"meditation/杂阿含/第十四章 将五种祸患关进笼子.md","filePath":"meditation/杂阿含/第十四章 将五种祸患关进笼子.md"}'),o={name:"meditation/杂阿含/第十四章 将五种祸患关进笼子.md"};function _(s,a,i,d,p,c){return r(),n("div",null,a[0]||(a[0]=[e("h1",{id:"第十四章-将五种祸患关进笼子",tabindex:"-1"},[l("第十四章 将五种祸患关进笼子 "),e("a",{class:"header-anchor",href:"#第十四章-将五种祸患关进笼子","aria-label":'Permalink to "第十四章 将五种祸患关进笼子"'},"​")],-1),e("p",null,"完全读懂杂阿含经",-1),e("p",null,"有个时候，佛陀住在拘睒弥国的瞿师罗园，这时，佛陀告诉诸弟子们：“就比如，有位书生，进入一间空房子里，遇见六种动物，最先遇见的是一条野狗，书生怕这条野狗咬人便将野狗关进了笼子，之后还遇到一只秃鹫鸟，一条毒蛇，一只鬣狗，一条鳄鱼，一只猕猴。书生把这些危险的动物全部都关进了笼子里。",-1),e("p",null,"野狗喜欢在旷野，村庄中穿行；秃鹫鸟喜欢翱翔于天空之中；毒蛇喜欢潜伏在洞穴之中；鬣狗喜欢在坟墓之间寻找腐肉；鳄鱼喜欢游弋于江河之中，猕猴喜欢在山林之间游玩。这六种动物，各自有喜欢去的地方，现在全部都被关进了笼子里，无法去它们喜欢呆的地方。同样的，眼睛、耳朵、鼻子、舌头、身体、心思，各自都有喜欢和不喜欢的东西，眼睛看见喜欢的东西，就生出喜悦的心情，看见不喜欢的东西，就生出厌恶的心情；耳朵听见悦耳动听的声音，就生出雀跃的心情，听见难听的声音，就生出厌烦的心情；鼻子闻到芳香四溢的气味，就生起怡悦的心情，闻到臭气熏天的气味，就生起讨厌的心情；舌头尝到清爽可口的美味，就生起欢喜的心情，尝到苦口难咽的食物，就生起憎恶的心情；身体摸到细滑柔嫩的东西，就生起愉悦的心情，摸到粗糙刺痛的东西，就生起憎恨的心情；心思想到情深意重的事情，就生起依依不舍的心情，想到深恶痛绝的事情，就生起愤恨不平的心情。",-1),e("p",null,"眼睛、耳朵、鼻子、舌头、身体、心思很想到它们喜欢的境界里去，比如眼睛想看好看的事物，耳朵想听好听的声音，鼻子想闻好闻的气味，舌头想尝好吃的东西，身体想摸细滑柔嫩的东西，心思想想梦寐以求的事情。弟子们，刚才我所说的野狗、秃鹫鸟、毒蛇、鬣狗、鳄鱼、猕猴六种动物就相当于是眼睛、耳朵、鼻子、舌头、身体、心思。要将眼睛、耳朵、鼻子、舌头、身体、心思也要全部关进笼子里，怎么做才是将它们关进笼子里呢？那就是看见喜欢的事物，不喜欢的事物，不生起喜欢、不喜欢的心念，不将心念拘泥于这些事物上，也就是看见一切事物不生起心念，如果生起了心念，也要即刻告诉自己这些都是随时在变化，无法长久存在，是虚假、不真实的事物，比如我们的身体要老化、生病，最后死去，不能长生不死，所以世间的一切都是随时在变化的，没有一个长久不变的事物。都是虚妄、虚假、不真实的事物，以此来断除掉已经生起的心念。",-1),e("p",null,"同样的。耳朵听见好听、不好听的声音；鼻子闻到好闻、不好闻的气味，舌头尝到好吃、不好吃的味道，身体摸到细滑柔嫩、粗糙刺痛的东西，心思想到梦寐以求、深恶痛绝的事情，也不生起喜欢、不喜欢的念头，若已生起念头也要立刻除灭，这样就是将眼睛、耳朵、鼻子、舌头、身体、心思关进了笼子里，弟子们你们要经常这样的将眼睛、耳朵、鼻子、舌头、身体、心思关进笼子里，才能不被外面境界的变化所影响，才能让内心清净无染、安乐自在，得到解脱。”",-1),e("p",null,"佛陀说法圆满，弟子们再次顶礼佛陀，都欢喜的按着佛陀的教法修行。",-1)]))}const f=t(o,[["render",_]]);export{u as __pageData,f as default};
