import{_ as t,c as s,a2 as p,o as B}from"./chunks/framework.CqbvlPrP.js";const a="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/1.jpg",A="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/2.jpg",o="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/3.jpg",r="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/4.jpg",e="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/5.jpg",i="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/6.jpg",c="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/7.jpg",n="/assets/img/stock/%E4%BA%A4%E6%98%93%E4%B8%AD%E5%A6%82%E4%BD%95%E5%81%9A%E5%88%B0%E7%AD%89%E5%88%B0%E6%97%B6%E6%9C%BA%E5%87%BA%E7%8E%B0/8.jpg",f=JSON.parse('{"title":"交易中如何做到等到时机出现","description":"","frontmatter":{},"headers":[],"relativePath":"stock/建仓/交易中如何做到等到时机出现.md","filePath":"stock/建仓/交易中如何做到等到时机出现.md"}'),_={name:"stock/建仓/交易中如何做到等到时机出现.md"};function m(g,E,l,D,d,h){return B(),s("div",null,E[0]||(E[0]=[p('<h1 id="交易中如何做到等到时机出现" tabindex="-1">交易中如何做到等到时机出现 <a class="header-anchor" href="#交易中如何做到等到时机出现" aria-label="Permalink to &quot;交易中如何做到等到时机出现&quot;">​</a></h1><p>进早了，价格不上不下，最终被止损，红着眼盯着K线。</p><p>进晚了，价格突然发力，进在半山腰，盯着K线拍断大腿。</p><p>像不像平时做交易的你？。。</p><p>摸到门道的交易者，自然知道如何等待进场的时机----</p><p><strong>知道进的早了，就要承认胜率给盈亏比让步；</strong></p><p><strong>知道进的晚了，就要默许胜率是大于盈亏比；</strong></p><p><strong>进的刚刚好，就是不论最终盈或损，都平常心看待，下一次依然按照自己的步子进，不早不晚就是刚刚好</strong>。</p><p>为何？因为这是在市场里一而再再而三被打脸打出来的。</p><p>还未摸到门道的投机分子，是不是不被反复打脸就没法学会【等待时机】，当然不是，起码可以少被打脸-----</p><p>知道为啥等？知道哪里等？等待的这个进场点所面临的风险报偿如何？------反复训练就是了，不妨就从几个基本形态练习？这里举两个例子：</p><h2 id="一-123法则" tabindex="-1">一.123法则： <a class="header-anchor" href="#一-123法则" aria-label="Permalink to &quot;一.123法则：&quot;">​</a></h2><p>简单说下123代表的含义：</p><p>1:突破趋势线</p><p>2:回调不再破之前趋势的极值</p><p>3.突破前高或前低。</p><p>如图： <img src="'+a+'" alt="图片"></p><p>形态有3个可以进场位置，各点进场所面临的胜率，利润空间各有不同。</p><p>如图： <img src="'+A+'" alt="图片"></p><h2 id="二-矩形【箱体】" tabindex="-1">二.矩形【箱体】 <a class="header-anchor" href="#二-矩形【箱体】" aria-label="Permalink to &quot;二.矩形【箱体】&quot;">​</a></h2><p>形态结构如图： <img src="'+o+'" alt="图片"></p><p>进场分为【突破后进场】和【突破前进场】</p><p>【突破后进场】---</p><p>2种情况如图： <img src="'+r+'" alt="图片"></p><p>2种止损位置：</p><p><img src="'+e+'" alt="图片"></p><p>【突破前进场】---</p><p>2种情况如图：</p><p><img src="'+i+'" alt="图片"></p><p>2种止损位置： <img src="'+c+'" alt="图片"></p><p>利润空间： <img src="'+n+'" alt="图片"></p><p>不同的位置，面临的胜率，盈亏比，包括频率，各有不同，该等待那种时机，取决于你练习的结果，那种可以慢慢盈利的结果。</p>',32)]))}const u=t(_,[["render",m]]);export{f as __pageData,u as default};
