import{_ as p,c as t,a2 as s,o as a}from"./chunks/framework.CqbvlPrP.js";const B="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/1.jpg",A="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/2.jpg",r="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/3.jpg",o="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/4.jpg",n="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/5.jpg",e="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/6.jpg",i="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/7.jpg",c="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/8.jpg",g="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/9.jpg",h="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/10.jpg",l="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/11.jpg",m="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/12.jpg",_="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/13.jpg",u="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/14.jpg",D="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/15.jpg",C="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/16.jpg",b="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/17.jpg",d="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/18.jpg",k="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/19.jpg",f="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/20.jpg",q="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/21.jpg",j="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/22.jpg",z="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%A6%82%E4%BD%95%E7%B2%BE%E7%A1%AE%E4%BB%8B%E5%85%A5%E5%92%8C%E5%8D%96%E5%87%BA/23.jpg",V=JSON.parse('{"title":"缠论如何精确介入和卖出","description":"","frontmatter":{},"headers":[],"relativePath":"stock/操作/缠论如何精确介入和卖出.md","filePath":"stock/操作/缠论如何精确介入和卖出.md"}'),x={name:"stock/操作/缠论如何精确介入和卖出.md"};function P($,E,v,N,S,J){return a(),t("div",null,E[0]||(E[0]=[s('<h1 id="缠论如何精确介入和卖出" tabindex="-1">缠论如何精确介入和卖出 <a class="header-anchor" href="#缠论如何精确介入和卖出" aria-label="Permalink to &quot;缠论如何精确介入和卖出&quot;">​</a></h1><h2 id="一、-一笔操作的精确买入卖出方法。" tabindex="-1">一、 一笔操作的精确买入卖出方法。 <a class="header-anchor" href="#一、-一笔操作的精确买入卖出方法。" aria-label="Permalink to &quot;一、 一笔操作的精确买入卖出方法。&quot;">​</a></h2><ol><li>一笔操作，核心就是判断一笔结束，然后买入。</li></ol><p><strong>注意，判断一笔结束，只需要看一个级别，不需要很多个级别来回切换</strong>，比如你的操作级别的日线，你就看日线操作，不需要再去看30分钟5分钟之类的。</p><p>那判断一笔结束，很简单，这里只讲2个，详细的大家去看之前的文章就好了。</p><p><strong>判断一笔结束就是看次级别背驰，如果次级别没有背驰就看验证分型</strong>，具体操作如下：</p><p><img src="'+B+'" alt="图片"></p><p>这里注意一下，背驰买入的，买入以后如果亏钱了，本人是建议补仓的。如果是验证底分型买入的，买入以后亏钱了，建议止损。</p><p><img src="'+A+'" alt="图片"></p><p><img src="'+r+'" alt="图片"></p><p>补仓以后要注意一点，那就是反弹一笔可能要结束或者已经确认结束，就要走人。</p><p>当然，实际上你也可以不补仓，设置止损的，但具体你自己去权衡一下就好了，不属于精确买入的范畴了，所以就不再讲了。</p><ol start="2"><li>卖出</li></ol><p><strong>标准的卖出是等上涨一笔以后次级别背驰卖出或者次级别2卖卖出，注意，这里是看裸k</strong>。</p><p><img src="'+o+'" alt="图片"></p><p>实际上呢，很多走势都不标准，很多都是盘整走势和小转大走势，所以我们如果一直等着背驰或者次级别2卖，可能很多时候利润会回吐很多，<strong>这个时候我们要加入主动止盈策略来辅助我们卖出。</strong></p><h2 id="如何主动止盈呢" tabindex="-1">如何主动止盈呢？ <a class="header-anchor" href="#如何主动止盈呢" aria-label="Permalink to &quot;如何主动止盈呢？&quot;">​</a></h2><p>本人之前讲的趋势战法中讲过，<strong>我们可以用05均线和10均线辅助我们操作，平时我用05均线比较多</strong>。</p><p>这两根均线怎么用呢？我们调出这两根均线以后，我们要看对于<strong>股价支撑的均线</strong>是哪根，我们就关注这根均线，当<strong>收盘价跌破这根均线</strong>的时候我们就可以卖出了。</p><p><strong>注意，这是应对小转大的方法</strong>，尤其是对加速上涨的走势比较有用。当然，如果你盈利以后发现<strong>走势很明显的背驰了，这个时候你就没必要等着收盘价跌破均线了。</strong></p><p>（这里有一个技巧，震荡缓慢上行可以看10均线甚至20均线，如果是脉冲式加速上涨可以看05均线）</p><p><img src="'+n+'" alt="图片"><img src="'+e+'" alt="图片"><img src="'+i+'" alt="图片"></p><p>总之没那么死板，火候的把握上大家要多多练习。</p><h2 id="二、-常规买卖点的精确买入卖出方法" tabindex="-1">二、 常规买卖点的精确买入卖出方法 <a class="header-anchor" href="#二、-常规买卖点的精确买入卖出方法" aria-label="Permalink to &quot;二、 常规买卖点的精确买入卖出方法&quot;">​</a></h2><p>常规买卖点就是<strong>缠论讲的1买2买3买</strong>的买入卖出方法，<strong>就是用区间套的买入卖出方法。</strong></p><p>这里注意，<strong>本人对于区间套的运用，一般只缩小一个级别</strong>，比如日线买卖点，我最多利用30分钟走势去做，我是不会去5分钟甚至1分钟看的，我也不建议大家无限的缩小级别去用区间套。</p><p>再注意一下，3个买卖点，买入卖出逻辑是一样的，所以本人只拿3个买卖点中的一个举例子。</p><ol><li>日线买点介入方法。</li></ol><p>案例，设研院日线3买入场方法。</p><p><img src="'+c+'" alt="图片"></p><p>比如我们要抓上图的这个3买，如何入场呢？</p><p>我们要切到30分钟，然后在30分钟买点区域，<strong>也就是1买和2买区域出现底分型停顿或者验证底分型的时候买入</strong>，止损点是底分型最低点。</p><p><img src="'+g+'" alt="图片"></p><p>这里的关键是，<strong>日线构筑买点的时候，30分钟一定是要处于买点区域，也就是构筑1买2买的时候出现底分型停顿或者验证底分型了才可以买入，这就是精确买入。</strong></p><ol start="2"><li>卖出</li></ol><p><strong>30分钟中枢背驰或者2卖区域，出现顶分型停顿或者验证顶分型卖出</strong>。</p><p><img src="'+h+'" alt="图片"></p><p>实际上，卖出的时候我们可以灵活运用，比如中枢背驰卖一部分，回到中枢接回来，2卖卖一部分，又或者趋势背驰全部走人等等都可以。</p><p>关于顶分型停顿和验证顶分型卖出，我们是做不到绝对卖在最高点的，有的时候也会卖丢了。</p><p>更加精确的可以用我之前讲的方法。</p><p><strong>那就是我们看着5分钟，等5分钟背驰的时候再去看30分钟有没有背驰，这样的话会更加精确</strong>，反正逻辑都是一样的，大家熟悉了根本不是问题，这里主要是对于买卖点级别混乱，搞清楚怎么买入卖出的朋友讲的。</p><h2 id="三、-针对本级别中枢的次级别3买精确买入卖出方法。" tabindex="-1">三、 针对本级别中枢的次级别3买精确买入卖出方法。 <a class="header-anchor" href="#三、-针对本级别中枢的次级别3买精确买入卖出方法。" aria-label="Permalink to &quot;三、 针对本级别中枢的次级别3买精确买入卖出方法。&quot;">​</a></h2><p><img src="'+l+'" alt="图片"></p><p><strong>这个方法介入方法有2种</strong>，</p><blockquote><p>第一个中枢小级别走势的，比如1分钟5分钟这种，我们可以直接看着本级别操作。</p></blockquote><p>买入点就是突破本级别中枢以后，出现第一个没有进入中枢的强势底分型，<strong>快要收盘或者收住以后就买入</strong>，止损点是跌破底分型最低点的时候。</p><p><img src="'+m+'" alt="图片"></p><p>卖出的话如果是加速上涨的，可以收盘价跌破05均线卖出，也可以验证顶分型卖出。如果支撑均线是10均线，那就收盘价跌破10均线卖出，一般05均线比价多。</p><p><img src="'+_+'" alt="图片"></p><blockquote><p>第二种买入方法就是区间套买入，这种是针对大级别走势讲的，<strong>比如针对日线中枢的次级别3买，针对周线中枢的次级别3买</strong>等等。</p></blockquote><p><strong>这个买入方法就是常规的区间套</strong>，比如针对日线的次级别3买，我们要去30分钟看30分钟下跌一笔什么时候结束，这个很简单，就是用我前面讲的判断一笔结束的方法或者用直接看30分钟底分型停顿验证底分型买入就好了，本人是比较建议使用前段时间我讲的判断一笔下跌结束的方法去操作比较好。</p><p><img src="'+u+'" alt="图片"></p><p>卖出的话其实我们做30分钟一笔就可以了，也就是30分钟上涨一笔结束卖出，那和开头讲的一笔卖出方法是一样的。</p><p>就是标准的等背驰，然后用05和10均线甚至20均线辅助持仓和主动止盈。</p><p>也可以做30分钟线段，因为我们其实是做日线上涨一笔延续，那我们是可以等日线上涨一笔结束走人的。</p><p>这里的关键就是你是要做30分钟一笔还是日线一笔？这个自己权衡一下就可以了，逻辑都是一样的。</p><p>30分钟一笔</p><p><img src="'+D+'" alt="图片"></p><p>日线</p><p><img src="'+C+'" alt="图片"></p><h2 id="四、-针对本级别中枢的本级别3买精确买入卖出方法。" tabindex="-1">四、 针对本级别中枢的本级别3买精确买入卖出方法。 <a class="header-anchor" href="#四、-针对本级别中枢的本级别3买精确买入卖出方法。" aria-label="Permalink to &quot;四、 针对本级别中枢的本级别3买精确买入卖出方法。&quot;">​</a></h2><p>这个的话我刚刚讲的第二大点举例了，就不再重复了。</p><h2 id="五、-b-a的精确买入卖出方法。" tabindex="-1">五、 b-A的精确买入卖出方法。 <a class="header-anchor" href="#五、-b-a的精确买入卖出方法。" aria-label="Permalink to &quot;五、 b-A的精确买入卖出方法。&quot;">​</a></h2><p><strong>b-A和针对本级别中枢的次级别3买是一样的，是同样的级别和方法</strong>。</p><p><strong>买卖方法同样也是两种方法。</strong></p><blockquote><p>第一种方法，针对小级别的，比如日内交易这种。主力注意一下，针对本级别中枢的次级别3买和b-A的区别在意针对本级别中枢的次级别3买的第一个底分型不进中枢，b-A的第二个底分型不进中枢，且这个底分型和第一个底分型重叠，如下图所示。</p></blockquote><p><img src="'+b+'" alt="图片"></p><p>买入也是一样的。</p><p>第二个强势底分型的时候买入，止损设置在底分型最低点。</p><p><img src="'+d+'" alt="图片"></p><p>卖出还是一样的。</p><p>标准的等次级别背驰，不标准的可以用05均线和10均线辅助卖出，或者验证顶分型卖出。</p><p><img src="'+k+'" alt="图片"></p><blockquote><p>第二种方法，就是用区间套买入卖出，其实又回到一笔操作上了，很简单的。</p></blockquote><p>我们首先要确定中枢的级别，比如栖霞建设。日线看有一个b-A，我们要确定一下这个b-A的级别。</p><p><img src="'+f+'" alt="图片"></p><p>这个其实是次级别中枢，我们看一眼就知道了，用我之前讲的k线看图技巧去看就可以了。</p><p>既然这个b-A是30分钟的，那我们操作级别就是30分钟，我们在30分钟中枢第三笔回调结束的时候买入。</p><p><img src="'+q+'" alt="图片"></p><p>如何判断这笔结束呢？这不又回到了我这段时间一直在讲的判断一笔结束的方法上了吗？太简单了一点。</p><p>卖出。</p><p>卖出的话我们的操作级别是30分钟，所以我们先看30分钟这笔上涨以后会不会背驰，没有背驰就不走，如果背驰了就走人。</p><p>当然，如果你操作一笔，比如就只要30分钟上涨一笔，那又回到了判断一笔结束上，还是次级别背驰，验证顶分型、05均线和10均线这些东西了。</p><p>30分钟一笔</p><p><img src="'+j+'" alt="图片"></p><p>30分钟一段</p><p><img src="'+z+'" alt="图片"></p><h2 id="六、-表里关系。" tabindex="-1">六、 表里关系。 <a class="header-anchor" href="#六、-表里关系。" aria-label="Permalink to &quot;六、 表里关系。&quot;">​</a></h2><p>表里关系，就是大级别一笔定位方向，次次级别顺着大级别笔操作。</p><p>具体我不讲了啊，不然表里关系又要讲几千上万字，具体大家看一下这两篇文章。</p><p>缠论表里关系、买卖点级别定位混乱---交易那些事儿 - <em>白仪</em>的文章 - 知乎 <a href="https://zhuanlan.zhihu.com/p/488745973" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/488745973</a></p><p>为数不多能让我专注研究的交易思路分享--交易那些事儿 - <em>白仪</em>的文章 - 知乎 <a href="https://zhuanlan.zhihu.com/p/83882586" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/83882586</a></p><p>关于缠论3买（卖）日内交易精确操作再讲---交易那些事儿 - <em>白仪</em>的文章 - 知乎 <a href="https://zhuanlan.zhihu.com/p/95672740" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/95672740</a></p><p>A股去年到现在涨幅超过60%的股票已经高达1千只啦，你的收益怎么样？----交易那些事儿 - <em>白仪</em>的文章 - 知乎 <a href="https://zhuanlan.zhihu.com/p/149171605" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/149171605</a></p><p>缠论之多级别联立---交易那些事儿 - <em>白仪</em>的文章 - 知乎 <a href="https://zhuanlan.zhihu.com/p/87314228" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/87314228</a></p><p>上面这些都是涉及表里关系的，其他还有很多文章就不一一讲了，大家可以去看之前的文章。</p><p>表里关系这块除了文中讲的以外，延伸的东西大家可以把我这段时间讲的一笔操作技巧加进去，因为表里关系的表就是大级别一笔，所以掌握了判断一笔结束的技巧，其实就是掌握了判断大方向的方法。</p>',97)]))}const w=p(x,[["render",P]]);export{V as __pageData,w as default};
