import{_ as t,c as e,a2 as a,o}from"./chunks/framework.CqbvlPrP.js";const m=JSON.parse('{"title":"不一样的缠论（高阶）3","description":"","frontmatter":{},"headers":[],"relativePath":"stock/缠论印证/不一样的缠论/不一样的缠论3.md","filePath":"stock/缠论印证/不一样的缠论/不一样的缠论3.md"}'),r={name:"stock/缠论印证/不一样的缠论/不一样的缠论3.md"};function s(n,p,c,i,_,d){return o(),e("div",null,p[0]||(p[0]=[a('<h1 id="不一样的缠论-高阶-3" tabindex="-1">不一样的缠论（高阶）3 <a class="header-anchor" href="#不一样的缠论-高阶-3" aria-label="Permalink to &quot;不一样的缠论（高阶）3&quot;">​</a></h1><p>价格与均线的关系</p><p>市场经济学的原理告诉我们：价格围绕价值进行上下波动。</p><p>我们转变一下思路，用均线来代替这段周期内的平均价值，得出来的结论就是：价格围绕均线进行上下波动。</p><p>把这个结论放回到市场中去检验一下，发现这个结论就是千真万确，的确如此！</p><p>这个结论与缠论什么关系呢？</p><p>打开任意一个市场品种的走势图，设置任意一条均线，仔细体会一下价格的趋势与均线之间的关系。</p><p>归纳起来，就是五种关系：</p><p>（1）价格上穿均线；</p><p>（2）价格从上方向下回抽均线；</p><p>（3）价格下穿均线；</p><p>（4）价格从下方向上回抽均线；</p><p>（5）价格与均线胶着，没有趋势和方向。</p><p>第五种情况的标的，都是垃圾，看都不要看一眼。我们只看前面四种情况。</p><p>第一种情况，价格上穿均线，这就是缠论所讲的上涨趋势啊！</p><p>第二种情况，价格从上方向下回抽均线，这就是盘整走势啊，也可以看成一个大级别的中枢结构啊！</p><p>第三种情况，与第一种情况相反，价格下穿均线，这就是下跌趋势。</p><p>第四种情况，与第二种情况相反，这也是盘整走势，可以看作下跌中的一个大级别的中枢结构。</p><p>缠论所讲的趋势、盘整结构类型，本质上来说，都是对价格围绕价值进行上下波动的情况分类。</p>',19)]))}const f=t(r,[["render",s]]);export{m as __pageData,f as default};
