import{_ as s,c as a,a2 as o,o as e}from"./chunks/framework.CqbvlPrP.js";const p="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/1.jpg",r="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/2.jpg",i="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/3.jpg",E="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/4.jpg",c="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/5.jpg",n="/assets/img/chan/%E7%BC%A0%E8%AE%BA%E5%8D%B0%E8%AF%81/%E7%A9%BA%E9%97%B4/6.jpg",f=JSON.parse('{"title":"交易系统中的概念——空间","description":"","frontmatter":{},"headers":[],"relativePath":"stock/缠论印证/空间理解.md","filePath":"stock/缠论印证/空间理解.md"}'),A={name:"stock/缠论印证/空间理解.md"};function l(B,t,m,_,g,h){return e(),a("div",null,t[0]||(t[0]=[o('<h1 id="交易系统中的概念——空间" tabindex="-1">交易系统中的概念——空间 <a class="header-anchor" href="#交易系统中的概念——空间" aria-label="Permalink to &quot;交易系统中的概念——空间&quot;">​</a></h1><p>空间是经常被提及的一个概念，<strong>有了空间的概念，才知道自己所做单子的性质，才能预估出盈亏比</strong>，单子持有到什么位置，不然总是对利润抱有不切实际的幻想，造成很多持仓问题，我把空间分为以下几类。</p><p><img src="'+p+'" alt="图片"></p><h2 id="空间分类" tabindex="-1">空间分类 <a class="header-anchor" href="#空间分类" aria-label="Permalink to &quot;空间分类&quot;">​</a></h2><ol><li>突破空间</li></ol><p>上图中，3到4的距离为突破空间，也就是价格向上运动后打开的一段空间，这段空间在概率上是有优势的，因为价格向上波动一段距离后，一般就会有朝上波动的第二次尝试，即使不能再创新高。</p><p>虽然我们 不做逆势单，但用下跌趋势中的上涨举例更能说明问题。</p><p><img src="'+r+'" alt="图片"> 突破空间</p><ol start="2"><li>乖离空间</li></ol><p>突破空间的利润显然是不够的，没有足够的盈亏比，我们总是期待价格能突破前高，那上方是有空间吗？</p><p>假设该形态处于趋势中的这个位置，那上方就有乖离空间，也就是更大级别的B浪空间，或者更大级别abc浪走完之后的反弹空间。</p><p><img src="'+i+'" alt="图片"> 乖离空间 <img src="'+E+'" alt="图片"> 乖离空间</p><ol start="3"><li>共振空间</li></ol><p>共振空间很好理解，就是上方是大级别的突破空间，如图所示。</p><p><img src="'+c+'" alt="图片"> 共振空间 <img src="'+n+'" alt="图片"> 共振空间</p><p>多周期之间的关系是最难处理的，理解了上方对空间的定义以及分类，就能够理解多周期之间的关系，都是干货，还望多思考。</p>',16)]))}const u=s(A,[["render",l]]);export{f as __pageData,u as default};
