import{_ as t,c as n,j as e,a as l,o as r}from"./chunks/framework.CqbvlPrP.js";const u=JSON.parse('{"title":"第三百五十章 四禅的境界","description":"","frontmatter":{},"headers":[],"relativePath":"meditation/巴利文大藏经/第三百五十章 四禅的境界.md","filePath":"meditation/巴利文大藏经/第三百五十章 四禅的境界.md"}'),o={name:"meditation/巴利文大藏经/第三百五十章 四禅的境界.md"};function s(_,a,i,d,p,c){return r(),n("div",null,a[0]||(a[0]=[e("h1",{id:"第三百五十章-四禅的境界",tabindex:"-1"},[l("第三百五十章 四禅的境界 "),e("a",{class:"header-anchor",href:"#第三百五十章-四禅的境界","aria-label":'Permalink to "第三百五十章 四禅的境界"'},"​")],-1),e("p",null,"完全读懂巴利文大藏经",-1),e("p",null,"相应部28相应4经/平静经(舍利弗相应/蕴篇/弟子记说)",-1),e("p",null,"有个时候，舍利弗尊者住在舍卫城的祇树林给孤独园，有一天，阿难尊者来到舍利弗尊者的住处，他与舍利弗尊者互相问候后，就对舍利弗尊者说：“舍利弗学友，如何进入四禅的境界呢？”",-1),e("p",null,"舍利弗尊者说：“阿难学友，如果想要进入四禅的境界，首先要先进入三禅的境界（三禅境界解释，见第三百四十九章），阿难学友！进入三禅境界后，逐渐除灭快乐、痛苦，逐渐除灭喜悦、忧愁等等喜怒哀乐的念想，由此进入不苦不乐平静安宁的境界，这样的清净境界，世尊称为四禅，简单的说就是：进入三禅后，灭尽喜乐、痛苦、忧愁等等喜怒哀乐的感受，进入不苦不乐的清净境界，这样的清净境界就是四禅。当我进入四禅清净境界的时候，不会生起：「我进入四禅」，或者「我已经进入四禅」，或者「我已经从四禅的境界中出来」的念想，我不会去分别、区别四禅的有无和存在。”",-1),e("p",null,"阿难尊者说：“舍利弗学友，我明白了，在三禅境界的基础上，除灭掉喜乐、痛苦、忧愁等等喜怒哀乐的感受，进入不苦不乐平静安宁的境界，这样就能消减、消解有「我」真实、永远存在的见解，就能消减、消解对「我」的执着和挂念，就能消减、消解对世间事物的执着和挂念，逐渐除灭贪爱，这样当然就不会生起「我进入四禅」，或者「我已经进入四禅」，或者「我已经从四禅的境界中出来」的念想。”",-1),e("p",null,"舍利弗尊者说：“阿难学友！确实如此，看来您也明白如何进入四禅的境界了。”",-1),e("p",null,"舍利弗尊者说法后，阿难尊者随喜赞叹舍利弗尊者说法的无量功德，并按着舍利弗尊者所说的法去修行。",-1)]))}const f=t(o,[["render",s]]);export{u as __pageData,f as default};
