import{_ as t,c as n,j as e,a as l,o as r}from"./chunks/framework.CqbvlPrP.js";const u=JSON.parse('{"title":"第三百四十八章 二禅的境界","description":"","frontmatter":{},"headers":[],"relativePath":"meditation/巴利文大藏经/第三百四十八章 二禅的境界.md","filePath":"meditation/巴利文大藏经/第三百四十八章 二禅的境界.md"}'),o={name:"meditation/巴利文大藏经/第三百四十八章 二禅的境界.md"};function _(s,a,i,d,p,c){return r(),n("div",null,a[0]||(a[0]=[e("h1",{id:"第三百四十八章-二禅的境界",tabindex:"-1"},[l("第三百四十八章 二禅的境界 "),e("a",{class:"header-anchor",href:"#第三百四十八章-二禅的境界","aria-label":'Permalink to "第三百四十八章 二禅的境界"'},"​")],-1),e("p",null,"完全读懂巴利文大藏经",-1),e("p",null,"相应部28相应2经/无寻经(舍利弗相应/蕴篇/弟子记说)",-1),e("p",null,"有个时候，舍利弗尊者住在舍卫城的祇树林给孤独园，有一天，阿难尊者来到舍利弗尊者的住处，他与舍利弗尊者互相问候后，就对舍利弗尊者说：“舍利弗学友，如何进入二禅的境界呢？”",-1),e("p",null,"舍利弗尊者说：“阿难学友，如果想要进入二禅的境界，首先要先进入初禅的境界（初禅境界解释，见第三百四十七章），阿难学友进入初禅境界后，平息、熄灭内心中集中关注的那些不会生起贪欲、渴爱事物事情的念想，比如平息、熄灭集中注意的数呼吸念想，平息注意、观想树叶、雨滴、河流等等事物事情的念想，也就是熄灭、平息那些不会生起贪欲、渴爱事物事情的念想，因为内心集中关注的那些不会生起贪欲、渴爱事物事情的念想，是由外界的事物生起的，所以也可以说是将对外界事物细微的分别、区别也除灭掉了。平息对外界事物细微的分别、区别。不再分别、区别外界的事物，将心安住在一处，安住在一境、不散乱，由此进入喜乐的清净境界，这样的清净境界，世尊称为二禅，当然我进入这个喜乐清净境界的时候，不会生起：「我进入二禅」，或者「我已经进入二禅」，或者「我已经从二禅的境界中出来」的念想，我不会去分别、区别二禅的有无和存在。”",-1),e("p",null,"阿难尊者说：“舍利弗学友，我明白了，在初禅境界的基础上，平息、熄灭掉初禅境界时为了让内心清净集中关注的那些不会生起贪欲、渴爱的事物事情，也就是熄灭、平息对外界事物事情细微的分别、区别，这样就能消减、消解有「我」真实、永远存在的见解，就能消减、消解对「我」的执着和挂念，就能消减、消解对世间事物的执着和挂念。这其实是进一步将初禅境界中替换烦恼和痛苦的事物事情也熄灭、平息了，连对外界事物事情的细微分别、区别都除灭了，由此逐渐除灭贪爱，这样当然就不会生起「我进入二禅」，或者「我已经进入二禅」，或者「我已经从二禅的境界中出来」的念想。”",-1),e("p",null,"舍利弗尊者说：“阿难学友！确实如此，看来您也明白如何进入二禅的境界了。”",-1),e("p",null,"舍利弗尊者说法后，阿难尊者随喜赞叹舍利弗尊者说法的无量功德，并按着舍利弗尊者所说的法去修行。",-1)]))}const f=t(o,[["render",_]]);export{u as __pageData,f as default};
