import{_ as t,c as n,j as e,a as l,o as r}from"./chunks/framework.CqbvlPrP.js";const u=JSON.parse('{"title":"第七章 用什么才能洗干净罪业！","description":"","frontmatter":{},"headers":[],"relativePath":"meditation/杂阿含/第七章 用什么才能洗干净罪业.md","filePath":"meditation/杂阿含/第七章 用什么才能洗干净罪业.md"}'),o={name:"meditation/杂阿含/第七章 用什么才能洗干净罪业.md"};function _(s,a,i,d,p,c){return r(),n("div",null,a[0]||(a[0]=[e("h1",{id:"第七章-用什么才能洗干净罪业",tabindex:"-1"},[l("第七章 用什么才能洗干净罪业！ "),e("a",{class:"header-anchor",href:"#第七章-用什么才能洗干净罪业","aria-label":'Permalink to "第七章 用什么才能洗干净罪业！"'},"​")],-1),e("p",null,"完全读懂杂阿含经",-1),e("p",null,"有个时候，佛陀在拘萨罗游化，来到孙陀利河丛林边，有个婆罗门住在孙陀利河边。看见佛陀来了，就过来顶礼佛陀，之后婆罗门问佛陀：“世尊，您想到孙陀利河里洗浴吗？”佛陀说：“你问我是不是想到孙陀利河里洗浴，这个河水与其他地方的河水有什么不同吗？”",-1),e("p",null,"婆罗门回答道：“孙陀利河水当然与其他普通的河水不同，孙陀利河水可以让洗浴后的人得到吉祥快乐，并能让洗浴的人清净解脱，除去一切罪恶。”",-1),e("p",null,"这时，佛陀说到：“在孙陀利河，婆休多河，伽耶萨罗河洗浴不能除去罪恶，不能让洗浴的人清净。很多愚痴的人经常在孙陀利河，婆休多河，伽耶萨罗河洗浴以为能够除去他们以前所做的一切罪业。真正众善奉行，断除一切恶的清净人，不用在河水中洗浴以此来除去罪业。因为一切都是清净的行为，所以也不需要为清净的行为反省忏悔！",-1),e("p",null,"清净是因为自己的行为是清净的，是因为不杀生、不偷盗、不邪淫、不妄语、不饮酒。不杀生是不杀伤生命；不偷盗是不盗取别人的财物；不邪淫是不作夫妇以外的淫事；不妄语是不说欺诳骗人的话；不饮酒是不喝和不吃含有麻醉性的酒类及药物，由此不冒犯伤害别人，也让自己受到保护不被他人冒犯伤害；布施供养则可以除去心中的吝啬和贪婪，这些行为才能除去心中的污垢，就如同洗浴除去身上的污垢一样。",-1),e("p",null,"对于一切的众生经常生起慈悲心，慈爱众生并给与快乐，同感其苦，怜悯众生，并拔除其苦，这样就如同打一桶井水上来把自己污垢的身体洗浴干净！内心的自在清净，不是依靠外在的河水来洗干净的！身体上的污垢，可以用清水来洗干净，但是却无法让内心清净无染。”",-1),e("p",null,"这时，婆罗门听到佛陀的说法，顿然开悟，他再次顶礼佛陀，欢喜随喜后离开。",-1)]))}const f=t(o,[["render",_]]);export{u as __pageData,f as default};
