import{_ as t,c as l,j as e,a as n,o}from"./chunks/framework.CqbvlPrP.js";const m=JSON.parse('{"title":"交易员必备的思维模式有哪些?","description":"","frontmatter":{},"headers":[],"relativePath":"stock/交易员必备的思维模式有哪些.md","filePath":"stock/交易员必备的思维模式有哪些.md"}'),r={name:"stock/交易员必备的思维模式有哪些.md"};function s(_,a,c,i,d,p){return o(),l("div",null,a[0]||(a[0]=[e("h1",{id:"交易员必备的思维模式有哪些",tabindex:"-1"},[n("交易员必备的思维模式有哪些? "),e("a",{class:"header-anchor",href:"#交易员必备的思维模式有哪些","aria-label":'Permalink to "交易员必备的思维模式有哪些?"'},"​")],-1),e("ol",null,[e("li",null,[e("p",null,"开仓就是试错 市场是不确定、无法准确预测未来的走势的，开仓后非涨即跌的两种不确定性走势，跟抛硬币的正反面一样具有随机性，如果抛硬币的次数足够多或一比一盈亏比交易次数足够多的情况下，成功率是无限接近50%的，跟闭眼赌大小没区别，所以开仓环节的成功率对长期稳定的赚钱产生不了优势，长期稳定赚钱交易员的成功率普遍在40%左右，但茫茫多的交易员大量的时间精力长期放在提高成功率上，而这并不是交易的核心。")]),e("li",null,[e("p",null,"截断亏损让利润奔跑 既然开仓成功率对于长期赚钱来说产生不了优势，那交易怎么玩，优势来源于哪里呢？长期赚钱的唯一优势来源是通过截断亏损让利润奔跑做高盈亏比的平仓逻辑，即使抛硬币决定多空，只要平仓环节能够截断亏损让利润奔跑也能实现最终的盈利。让利润奔跑并不容易，因为很多盈利不奔跑，这是很多交易员无法长期一致性执行的原因，但一旦改变就会把高盈亏比这个优势来源压缩在一个较小的空间里，长期赚钱就很难了。赚点就跑亏了死扛是嗷嗷待割韭菜的最爱，也是稳定亏损交易员70%以上高成功率的原因。")]),e("li",null,[e("p",null,"仓位管理 交易是控制风险的游戏，有效的仓位管理要在能保证不被不利期打死和收益之间找好平衡点，控制风险最有效的方法是降低仓位，但一味的轻仓并不可取，我们是来赚钱的啊，所以我建议赢冲输缩的仓位管理方式，亏了就把仓位降下来控制风险，等赚回来在恢复原仓位，如果赚钱了有了安全垫了就加大仓位扩大盈利。")])],-1)]))}const f=t(r,[["render",s]]);export{m as __pageData,f as default};
