import{_ as t,c as s,a2 as E,o as a}from"./chunks/framework.CqbvlPrP.js";const o="/assets/img/stock/%E4%BA%A4%E6%98%93%E7%B3%BB%E7%BB%9F%E5%8F%AF%E4%BB%A5%E6%9C%89%E5%A4%9A%E7%AE%80%E5%8D%95/1.jpg",e="/assets/img/stock/%E4%BA%A4%E6%98%93%E7%B3%BB%E7%BB%9F%E5%8F%AF%E4%BB%A5%E6%9C%89%E5%A4%9A%E7%AE%80%E5%8D%95/2.jpg",r="/assets/img/stock/%E4%BA%A4%E6%98%93%E7%B3%BB%E7%BB%9F%E5%8F%AF%E4%BB%A5%E6%9C%89%E5%A4%9A%E7%AE%80%E5%8D%95/3.jpg",B="/assets/img/stock/%E4%BA%A4%E6%98%93%E7%B3%BB%E7%BB%9F%E5%8F%AF%E4%BB%A5%E6%9C%89%E5%A4%9A%E7%AE%80%E5%8D%95/4.jpg",d=JSON.parse('{"title":"交易系统可以有多简单？","description":"","frontmatter":{},"headers":[],"relativePath":"stock/交易系统适应性/交易系统可以有多简单.md","filePath":"stock/交易系统适应性/交易系统可以有多简单.md"}'),c={name:"stock/交易系统适应性/交易系统可以有多简单.md"};function A(i,p,_,n,m,l){return a(),s("div",null,p[0]||(p[0]=[E('<h1 id="交易系统可以有多简单" tabindex="-1">交易系统可以有多简单？ <a class="header-anchor" href="#交易系统可以有多简单" aria-label="Permalink to &quot;交易系统可以有多简单？&quot;">​</a></h1><p>最简单的交易系统莫过于单均线系统了。</p><p>简单说，就是高于20日均线就买，低于就卖的策略。</p><p>简单是简单，但是能赚钱吗？ 能赚大钱吗？</p><p>拍脑袋没意义，我们拿数据说话。 <img src="'+o+'" alt="图片"></p><p>对5000只票，全日期回测，就用单均线这个简单策略来做。</p><p>测试结果一览：</p><p><img src="'+e+'" alt="图片"></p><p>我们点开最赚钱的：</p><p><img src="'+r+'" alt="图片"></p><p>一直持有4倍，使用策略 800倍！</p><p>怎么样，够简单吧，够暴力吧。</p><p>可是我们再随便点开不赚钱的： <img src="'+B+'" alt="图片"></p><p>一直持有也是4倍，使用策略 亏 97%！ 只剩下3%~</p><p>所以交易系统本身简单复杂，好像并不是问题的关键。</p><p>问题的关键是，什么品种，什么行情，应该用什么策略才能得到超额的收益。</p><p>有时候听到一个很好的策略案例，也许它是真实的，可它的 “真实”仅仅在某个品种的某种行情下有好的表现。</p><p>相同的策略同一个品种，换一段时期表现就大不一样了。</p><p>相同的策略，同一段时期，换一个品种，表现就大不一样了。</p><p>有没有这样简单的系统，对所有的品种所有的时期都能够有好的表现呢？</p><p>我认为是没有的，如果有，所有的人都会用这样的策略了。</p><p>假如真正所有的人都用这样一个简单且爆赚的系统，那么这个系统自然也就失效了。</p><p>所以我觉得我们学习交易，要两手抓。</p><p>左手，研究策略本身，它有什么优缺点。</p><p>右手，研究什么时候用什么策略，什么牌局打哪张牌。</p><p>不能单纯研究策略，刻舟求剑。</p><p>也不能盲目自信能够给行情找到最适合的策略，这样也做不到。</p><p><strong>未谋胜，先谋败，要假设这一次出手是错误的，计算最大亏损时多少。</strong></p><p>做好资金管理，把风险风散。</p><p>自己能亏多少，是能够计算的，是能够管理的。</p><p>自己能赚多少，是老天爷赏饭吃，是行情给的，不是自己有多能耐。</p>',31)]))}const F=t(c,[["render",A]]);export{d as __pageData,F as default};
